import React from 'react'
import styles from './Login.module.css'
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import validateUser from '../../commons/functions/validateUser';

function Login() {
  
  const navigate = useNavigate();
  const [errors, setErrors] = useState({});
  const [access, setAccess] = useState(false);
  const [password, setPassword] = useState("");
  const [gmail, setGmail] = useState("");
  const [userData, setUserData] = useState({
    gmail: "",
    password: "",
  });
  
  // const navigate = useNavigate();

  const handleChange = (event) => {
    setUserData({
      ...userData,
      [event.target.name]: event.target.value
    });

    setErrors(validateUser({
      ...userData,
      [event.target.name]: event.target.value
    }))
  }

  const handleSubmit = (event) => {
    event.preventDefault()
    const gmail = userData.gmail;
    const password = userData.password;
    if (gmail === "invitado@gmail.com" && password === "maradona1") { setAccess(true); navigate("/home") }
    else { alert ("Usuario o contrase√±a incorrecta") } 
  }

  return (
    <form className={styles.login} onSubmit={handleSubmit}>
      
      <div className={styles.img_container}>
        <img src="rickymorty.png" alt="Rick" />
      </div>
      <div>
        <label 
        htmlFor="ingresatusdatos" 
        className={styles.label}> 
        üëáüèº INGRESA TUS DATOS üëáüèº
        </label>

        <label htmlFor='text'>
          <input 
          type="gmail" 
          className={styles.input} 
          placeholder='E-mail'
          name="gmail"
          value={userData.gmail}
          onChange={handleChange}
          />
          {errors.gmail && <p>{errors.gmail}</p>}
        </label>

        <label htmlFor='text'>
          <input 
          type="password"
          className={styles.input} 
          placeholder='Password'
          name="password"
          value={userData.password}
          onChange={handleChange}
          />
          {errors.password && <p>{errors.password}</p>} 
        </label>

        <button 
        className={styles.button}
        onClick={handleSubmit}> 
        Login
        </button>
      </div>

    </form>
    
  )
}

export default Login;

